const skillsData = [
	{ name: "HTML", icon: <Html /> },
	{ name: "CSS", icon: <Css /> },
	{ name: "Git", icon: <Git /> },
	{ name: "React", icon: <ReactIcon /> },
	{ name: "PostgreSQL", icon: <PostgreSql /> },
	{ name: "MaterialUI", icon: <MaterialUI /> },
	{ name: "MongoDB", icon: <MongoDBIcon /> },
	{ name: "React", icon: <ReactIcon /> },
	{ name: "JavaScript", icon: <JavaScript /> },
];

const sectionVariants = {
	hidden: { opacity: 0 },
	visible: { opacity: 1, transition: { staggerChildren: 0.3 } },
};

const itemVariants = {
	hidden: { opacity: 0, y: 30 },
	visible: { opacity: 1, y: 0, transition: { duration: 0.6, ease: "easeOut" } },
};

const Skills = () => {
	const controls = useAnimation();
	const [ref, isOnScreen] = useOnScreen({ threshold: 0.2 });

	useEffect(() => {
		if (isOnScreen) {
			controls.start("visible");
		} else {
			controls.start("hidden");
		}
	}, [isOnScreen, controls]);

	return (
		<SkillsContainer id="habilidades" ref={ref}>
			<motion.div
				variants={sectionVariants}
				initial="hidden"
				animate={controls}
			>
				<SectionTitle variants={itemVariants}>Habilidades</SectionTitle>
				<MarqueeContainer variants={itemVariants}>
					<SkillTrack>
						{/* Renderizamos a lista de skills duas vezes para criar o loop contínuo */}
						{skillsData.map((skill, index) => (
							<SkillCard
								key={`skill-a-${
									// biome-ignore lint/suspicious/noArrayIndexKey: <explanation>
									index
								}`}
							>
								<SkillIcon>{skill.icon}</SkillIcon>
								<SkillName>{skill.name}</SkillName>
							</SkillCard>
						))}
						{skillsData.map((skill, index) => (
							<SkillCard
								key={`skill-b-${
									// biome-ignore lint/suspicious/noArrayIndexKey: <explanation>
									index
								}`}
							>
								<SkillIcon>{skill.icon}</SkillIcon>
								<SkillName>{skill.name}</SkillName>
							</SkillCard>
						))}
					</SkillTrack>
				</MarqueeContainer>
			</motion.div>
		</SkillsContainer>
	);
};

export default Skills;
